import React from "react";

const Rating = ({ value, text }) => {
  return (
    <div className="rating">
      <span>
        {" "}
        <i
          className={
            value >= 1
              ? "far fa-star"
              : value >= 0.5
              ? "far fa-star-half"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>
        {" "}
        <i
          className={
            value >= 2
              ? "far fa-star"
              : value >= 1.5
              ? "far fa-star-half"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>
        {" "}
        <i
          className={
            value >= 3
              ? "far fa-star"
              : value >= 2.5
              ? "far fa-star-half"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>
        {" "}
        <i
          className={
            value >= 4
              ? "far fa-star"
              : value >= 3.5
              ? "far fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>
        {" "}
        <i
          className={
            value >= 5
              ? "far fa-star"
              : value >= 4.5
              ? "far fa-star-half"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>{text && text}</span>
    </div>
  );
};

export default Rating;
